mapper.select:
select t.* from( select t.*,rownum rn from( SELECT t.*, t2.username tousername, t3.username createusername, t4.name orgname FROM cms_work t left join sys_user t2 on t.touser=t2.usercode left join sys_user t3 on t.createuser=t3.usercode left join sys_org t4 on t4.id=t3.orgid where 1=1
and t2.username like '%' || #{name} || '%'
and t.taskno like '%' || #{taskno} || '%'
and t.taskname like '%' || #{taskname} || '%'
AND t.createdate >= #{datestart} AND t.createdate <=#{dateend}
and (t.touser=#{touser} or t.createuser=#{touser})
and t.isfinish=#{isfinish}
and t4.name like '%' || #{orgname} || '%'
and t.createuser in (select usercode from sys_user where orgid in(select id from ggs.sys_org t start with t.id=#{orgid} connect by prior t.id=t.pid) )
order by t.createdate desc,t.id desc)t)t where t.rn>=#{start} and rownum<=#{limit}

select rownum, t.* from ( select t4.name orgname,t.usercode touser,t.username,nvl(t3.counter,0) counter from ggs.sys_user t left join ( select touser,count(*)counter from cms_work t where 1=1 AND t.createdate >= #{datestart} AND t.createdate <=#{dateend}
and t.touser=#{touser}
group by t.touser)t3 on t3.touser=t.usercode left join ggs.sys_org t4 on t4.id=t.orgid where 1=1
and t.orgid in
#{item}
order by counter desc)t
