mapper.select:
select t.* from( select t.*,rownum rn from( SELECT t.*, t2.username tousername, t3.username createusername, t4.name orgname FROM cms_work_goal t left join sys_user t2 on t.touser=t2.usercode left join sys_user t3 on t.createuser=t3.usercode left join sys_org t4 on t4.id=t3.orgid where 1=1
and t.goalname like '%' || #{goalname} || '%'
and t.goalno like '%' || #{goalno} || '%'
AND t.createdate >= #{datestart} AND t.createdate <=#{dateend}
and t.isfinish=#{isfinish}
and t.touser in (select usercode from sys_user where orgid in(select id from ggs.sys_org t start with t.id=#{orgid} connect by prior t.id=t.pid) )
order by t.goalno desc,t.id desc)t)t where t.rn>=#{start} and rownum<=#{limit}

SELECT
count(*)counter
FROM cms_work_goal  t where 1=1

    and t.goalname like '%' ||  #{goalname} || '%'


    and t.goalno like '%' || #{goalno} || '%'

AND t.createdate >= #{datestart}
AND t.createdate <=#{dateend}


    and t.isfinish=#{isfinish}


    and t.touser in (select usercode from sys_user where orgid  in(select id from ggs.sys_org t  start with t.id=#{orgid} connect by prior t.id=t.pid) )
